definition.script:
pipeline{
    agent {
        label 'jenkins-slave'
    }
    stages{
        stage('Checkout code'){
            steps{
                git 'https://github.com/wmsegar/bootcamp-jenkins.git'
            }
        }
        stage('Build Code') {
            steps{
                container('mvn'){
                    echo 'Code Building...'
                    script {
                        sh 'mvn -v'
                    }
                    sleep 20    
                }
            }
        }
        stage('Build Image'){
            steps{
                container('docker'){
                    echo 'Image Building...'
                    script{
                        sh 'docker'
                    }
                    sleep 20
                }
            }
        }
        stage('Push Image to Repo'){
            steps{
                container('docker'){
                    echo 'Pushing Image...'
                    script{
                        sh 'docker'
                    }
                    sleep 20
                }
            }
        }
        stage('Deploy to Staging'){
            steps{
                container('kubectl'){
                    echo 'Deployment canary build...'
                    script{
                        sh 'ls $WORKSPACE'
                        sh 'kubectl apply -f $WORKSPACE/manifests/sockshop-app/canary/carts2-canary.yml'
                    }
                }
            }
        }
    }
}
